import React, { useRef, useState} from 'react';
import { 
  IonApp, 
  IonContent, 
  IonHeader, 
  IonToolbar, 
  IonTitle, 
  IonGrid, 
  IonRow, 
  IonCol,
  IonItem,
  IonLabel,
  IonInput,
  IonCard,
  IonCardContent,
  IonAlert,
} from '@ionic/react';
import { calculatorOutline, refreshOutline } from 'ionicons/icons';

import BmiControls from './components/BmiControls';
import BmiResult from './components/BmiResult';
import InputControl from './components/InputControl';

/* Core CSS required for Ionic components to work properly */
import '@ionic/react/css/core.css';

/* Basic CSS for apps built with Ionic */
import '@ionic/react/css/normalize.css';
import '@ionic/react/css/structure.css';
import '@ionic/react/css/typography.css';

/* Optional CSS utils that can be commented out */
import '@ionic/react/css/padding.css';
import '@ionic/react/css/float-elements.css';
import '@ionic/react/css/text-alignment.css';
import '@ionic/react/css/text-transformation.css';
import '@ionic/react/css/flex-utils.css';
import '@ionic/react/css/display.css';

/* Theme variables */
import './theme/variables.css';


const App: React.FC = () => {

  const [calculatedBmi, setCalculatedBmi] = useState<number>();
  const [error, setError] = useState<string>();
  const [calcUnits, setCalcUnits] = useState<'mkg' | 'ftlbs'>('mkg');

  const weightInputRef = useRef<HTMLIonInputElement>(null);
  const heightInputRef = useRef<HTMLIonInputElement>(null);

const calculateBMI = () => {
  const enteredWeight = weightInputRef.current!.value;
  const enteredHeight = heightInputRef.current!.value;

  if(!enteredHeight || !enteredWeight || +enteredWeight <= 0 || +enteredHeight <= 0){
    setError('Please enter a valid (non-negative) input number')
    return;
  }

  const weightConversionFactor = calcUnits === 'ftlbs' ? 2.2 : 1;
  const heightConversionFactor = calcUnits === 'ftlbs' ? 3.28 : 1;

  const weight = +enteredWeight / weightConversionFactor;
  const height = +enteredHeight / heightConversionFactor

  const bmi =  +weight / (+enteredHeight * +enteredHeight);
  setCalculatedBmi(bmi);
};

const resetInput = () => {
  weightInputRef.current!.value = '';
  heightInputRef.current!.value = '';
};

const clearError = () => {
  setError('');
}

const selectCalcUnitHandler = (selectedValue : 'mkg' | 'ftlbs') => {
  setCalcUnits(selectedValue);
};

return (
  <React.Fragment>
    <IonAlert isOpen={!!error} message={error} buttons={[{text: 'Okay', handler: clearError }]}/>
  <IonApp>
   <IonHeader>
     <IonToolbar color="primary">
       <IonTitle>BMI Calculator</IonTitle>
     </IonToolbar> 
   </IonHeader>
   <IonContent className="ion-padding">
     <IonGrid>
     <IonRow>
       <IonCol>
         <InputControl 
         selectedValue={calcUnits} 
         onSelectValue={selectCalcUnitHandler}
         />
       </IonCol>
     </IonRow>
       <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Your Height ({calcUnits === 'mkg' ? 'meters' : 'ft'})</IonLabel>
            <IonInput ref={heightInputRef}></IonInput>
          </IonItem>
          </IonCol>
       </IonRow>
       <IonRow>
        <IonCol>
          <IonItem>
            <IonLabel position="floating">Your Weight ({calcUnits === 'mkg' ? 'kg' : 'lbs'}) </IonLabel>
            <IonInput ref={weightInputRef}></IonInput>
          </IonItem>
          </IonCol>
       </IonRow>

       <BmiControls onCalculate={calculateBMI} onReset={resetInput} />


{calculatedBmi && (
       <BmiResult result={calculatedBmi} />
)}
     </IonGrid>

   </IonContent>
  </IonApp>
  </React.Fragment>
);
}

export default App;
